package week1.day1;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = -40,a=0;
		if (i < 0) {
			a = i - (i * 2);
			System.out.println("The number " + i +" is converted to " + a);
		} else

			System.out.println(i + " is positive number");

	}

}
